mapper.select:
SELECT T1.ID AS ID , T1.MST_CORPNAME AS MST_CORPNAME , T1.MST_PARTNAME AS MST_PARTNAME , T1.MST_CREATE_USER AS MST_CREATE_USER , T1.MST_ADMIN_USER AS MST_ADMIN_USER , REPLACE(SPLIT_PART(T1.MST_REGISTERED, ' ',1),'/','-') AS MST_REGISTERED , (SELECT COUNT(*) FROM TN_FEIS_MSE_FIELDINFO T2 WHERE T2.MST_ID = T1.ID) TOTCNT FROM TN_FEIS_MSE_CNRSSPCE T1 WHERE 1=1
AND T1.ID = CAST(#{id} AS INT)
AND SPLIT_PART((SPLIT_PART(T1.MST_REGISTERED, ' ',1)),'/',1) = #{svy_year}
AND T1.MST_CORPNAME = #{mst_corpname}
AND T1.MST_PARTNAME like CONCAT ('%', #{mst_partname},'%')
AND T1.MST_CREATE_USER like CONCAT ('%', #{mst_create_user},'%')
ORDER BY T1.MST_REGISTERED DESC, T1.ID DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) TOTCNT FROM TN_FEIS_MSE_CNRSSPCE T1 WHERE 1=1
AND T1.ID = CAST(#{id} AS INT)
AND SPLIT_PART((SPLIT_PART(T1.MST_REGISTERED, ' ',1)),'/',1) = #{svy_year}
AND T1.MST_CORPNAME = #{mst_corpname}
AND T1.MST_PARTNAME like CONCAT ('%', #{mst_partname},'%')
AND T1.MST_CREATE_USER like CONCAT ('%', #{mst_create_user},'%')

mapper.insert:
	INSERT INTO TN_FEIS_MSE_CNRSSPCE
	(
		MST_CORPNAME
		, MST_PARTNAME
		, MST_CREATE_USER
		, MST_ADMIN_USER		
		, MST_REGISTERED
		, MST_PASSWORD
		, MST_ADMINPWD
		, MST_READPWD
	) VALUES (
		#{mst_corpname}
		, #{mst_partname}
		, #{mst_create_user}
		, #{mst_admin_user}
		, to_char(now(),'YYYY/MM/DD HH24:MI:SS')
		, #{mst_password}
		, #{mst_password}
		, #{mst_password}
    )

INSERT INTO TN_FEIS_MSE_FIELDINFO
( MST_ID,LOGIN_ID,SVY_KEYWORD,SVY_LABEL,SVY_LON,SVY_LAT,SMGEOMETRY,SVY_ATTR,CREAT_DT,SVY_MEMO )
VALUES

( 
	CAST(#{item.MST_ID} AS INT)
	, #{item.LOGIN_ID}
	, #{item._KEYWORD}
	, #{item._LABEL}
	, CAST(ST_X(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{item.LONDD},#{item.LATDD}),4326),5186)) AS FLOAT)
	, CAST(ST_Y(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{item.LONDD},#{item.LATDD}),4326),5186)) AS FLOAT)
	, ST_Transform(ST_SetSRID(ST_MakePoint(#{item.LONDD},#{item.LATDD}),4326),5186)
	, #{item.ATTR}
	, now()
	, #{item._MEMO}
)
ON CONFLICT ON CONSTRAINT TN_FEIS_MSE_FIELDINFO_UN
DO UPDATE
SET SVY_KEYWORD = #{item._KEYWORD}
	, SVY_LON = CAST(ST_X(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{item.LONDD},#{item.LATDD}),4326),5186)) AS FLOAT)
	, SVY_LAT = CAST(ST_Y(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{item.LONDD},#{item.LATDD}),4326),5186)) AS FLOAT)
	, SMGEOMETRY = ST_Transform(ST_SetSRID(ST_MakePoint(#{item.LONDD},#{item.LATDD}),4326),5186)
	, SVY_ATTR = #{item.ATTR}
	, LAST_UPDT_PNTTM = now()
	_MEMO = #{item._MEMO}

INSERT INTO TN_FEIS_CNRSSPCE_AUTHORMGT
( MST_ID,SVYTYPE,ESNTL_ID,USER_GRADE)
VALUES

( 
	CAST(#{item.id} AS INT)
	, #{item.svytype}
	, #{item.esntl_id}
	, #{item.user_grade}
)

INSERT INTO TN_FEIS_MSE_FIELDINFO( MST_ID
	,SVY_STEP
	,LOGIN_ID

	,SVY_KEYWORD
	,SVY_LABEL
	,SVY_LON
	,SVY_LAT
	,SVY_DATA
	,SVY_ATTR
	,CREAT_DT
	,LAST_UPDT_PNTTM
	,SVY_TAG1
	,SVY_MEMO )
VALUES
(
	CAST(#{mst_id} AS INT)
	, #{svy_step}
	, #{login_id}
	, #{svy_keyword}
	, #{svy_label}
	, CAST(#{svy_lon} AS FLOAT)
	, CAST(#{svy_lat} AS FLOAT)
	, #{smgeometry}

	, #{svy_attr}
	, NOW()
	, NOW()
	, #{svy_tag1}
	, #{svy_memo}
)

mapper.delete:

DELETE FROM TN_FEIS_MSE_FIELDINFO
WHERE MST_ID = CAST(#{id} as INT)
AND SVY_LABEL = #{label}

mapper.select:
SELECT USER_GRADE FROM TN_FEIS_CNRSSPCE_AUTHORMGT
WHERE MST_ID = #{id}
	AND ESNTL_ID = #{esntlId}

SELECT SLD_ID , SD , SGG , EMD , RI , JIBUN , OWNER , SVY_LON , SVY_LAT , SVY_DATA , LGL_LIMIT , SVY_MEMO::JSON->'와이어신축계' AS WIRE_EXT , JSON_ARRAY_LENGTH(SVY_MEMO::JSON->'와이어신축계') AS WIRE_EXT_CNT , SVY_MEMO::JSON->'지하수위계' AS GW_GAUGE , JSON_ARRAY_LENGTH(SVY_MEMO::JSON->'지하수위계') AS GW_GAUGE_CNT , SVY_MEMO::JSON->'강우계' AS RAIN_GAUGE , JSON_ARRAY_LENGTH(SVY_MEMO::JSON->'강우계') AS RAIN_GAUGE_CNT , SVY_MEMO::JSON->'구조물변위계' AS STRC_DPM , JSON_ARRAY_LENGTH(SVY_MEMO::JSON->'구조물변위계') AS STRC_DPM_CNT , SVY_MEMO::JSON->'지표변위계' AS SUR_DPM , JSON_ARRAY_LENGTH(SVY_MEMO::JSON->'지표변위계') AS SUR_DPM_CNT , SVY_MEMO::JSON->'GPS변위계' AS GPS_GAUGE , JSON_ARRAY_LENGTH(SVY_MEMO::JSON->'GPS변위계') AS GPS_GAUGE_CNT , SVY_MEMO::JSON->'게이트웨이' AS GATEWAY , JSON_ARRAY_LENGTH(SVY_MEMO::JSON->'게이트웨이') AS GATEWAY_CNT , SVY_MEMO::JSON->'노드' AS NODE , JSON_ARRAY_LENGTH(SVY_MEMO::JSON->'노드') AS NODE_CNT , SVY_MEMO::JSON->'지중경사계'->'외관점검' AS LIC_METER , JSON_ARRAY_LENGTH(SVY_MEMO::JSON->'지중경사계'->'외관점검') AS LIC_METER_CNT , SVY_MEMO::JSON->'지중경사계'->'성능점검' AS LIC_METER_PER FROM TN_FEIS_MSE_SLDINFO WHERE 1=1
AND SLD_ID NOT IN (SELECT CONCAT(SPLIT_PART(SVY_LABEL,'_',1),'_',SPLIT_PART(SVY_LABEL,'_',2)) AS SLD_ID FROM TN_FEIS_MSE_FIELDINFO WHERE SVY_STEP = #{svy_step})
AND SD = #{svySd}
AND SGG = #{svySgg}
AND EMD = #{svyEmd}
AND RI = #{svyRi}
AND JIBUN LIKE CONCAT('%', #{svyJibun}, '%')
