mapper.select:
SELECT T1.ID AS ID , T1.MST_CORPNAME AS MST_CORPNAME , T1.MST_PARTNAME AS MST_PARTNAME , T1.MST_CREATE_USER AS MST_CREATE_USER , T1.MST_ADMIN_USER AS MST_ADMIN_USER , REPLACE(SPLIT_PART(T1.MST_REGISTERED, ' ',1),'/','-') AS MST_REGISTERED , (SELECT COUNT(*) FROM TN_FEIS_CNL_FIELDINFO T2 WHERE T2.MST_ID = T1.ID) TOTCNT FROM TN_FEIS_CNL_CNRSSPCE T1 WHERE 1=1
AND T1.ID = CAST(#{id} AS INT)
AND SPLIT_PART((SPLIT_PART(T1.MST_REGISTERED, ' ',1)),'/',1) = #{svy_year}
AND T1.MST_CORPNAME = #{mst_corpname}
AND T1.MST_PARTNAME like CONCAT ('%', #{mst_partname},'%')
AND T1.MST_CREATE_USER like CONCAT ('%', #{mst_create_user},'%')
ORDER BY T1.MST_REGISTERED DESC, T1.ID DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) TOTCNT FROM TN_FEIS_CNL_CNRSSPCE T1 WHERE 1=1
AND T1.ID = CAST(#{id} AS INT)
AND SPLIT_PART((SPLIT_PART(T1.MST_REGISTERED, ' ',1)),'/',1) = #{svy_year}
AND T1.MST_CORPNAME = #{mst_corpname}
AND T1.MST_PARTNAME like CONCAT ('%', #{mst_partname},'%')
AND T1.MST_CREATE_USER like CONCAT ('%', #{mst_create_user},'%')

mapper.insert:
	INSERT INTO TN_FEIS_CNL_CNRSSPCE
	(
		MST_CORPNAME
		, MST_PARTNAME
		, MST_CREATE_USER
		, MST_ADMIN_USER		
		, MST_REGISTERED
		, MST_PASSWORD
		, MST_ADMINPWD
		, MST_READPWD
	) VALUES (
		#{mst_corpname}
		, #{mst_partname}
		, #{mst_create_user}
		, #{mst_admin_user}
		, to_char(now(),'YYYY/MM/DD HH24:MI:SS')
		, #{mst_password}
		, #{mst_password}
		, #{mst_password}
    )

INSERT INTO TN_FEIS_CNRSSPCE_AUTHORMGT
( MST_ID,SVYTYPE,ESNTL_ID,USER_GRADE)
VALUES

( 
	CAST(#{item.id} AS INT)
	, #{item.svytype}
	, #{item.esntl_id}
	, #{item.user_grade}
)

INSERT INTO TN_FEIS_CNL_FIELDINFO
( MST_ID,LOGIN_ID,SVY_KEYWORD,SVY_LABEL,SVY_LON,SVY_LAT,SVY_DATA,SVY_ATTR,CREAT_DT,SVY_MEMO )
VALUES

( 
	CAST(#{item.mst_id} AS INT)
	, #{item.login_id}
	, #{item.svy_keyword}
	, #{item.svy_label}
	, CAST(ST_X(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{item.londd},#{item.latdd}),4326),5186)) AS FLOAT)
	, CAST(ST_Y(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{item.londd},#{item.latdd}),4326),5186)) AS FLOAT)
	, ST_ASTEXT(ST_Transform(ST_SetSRID(ST_MakePoint(#{item.londd},#{item.latdd}),4326),5186))
	, #{item.svy_attr}
	, now()
	, #{item.svy_memo}
)
ON CONFLICT ON CONSTRAINT TN_FEIS_CNL_FIELDINFO_UN
DO UPDATE
SET SVY_KEYWORD = #{item.svy_keyword}
	, SVY_LON = CAST(ST_X(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{item.londd},#{item.latdd}),4326),5186)) AS FLOAT)
	, SVY_LAT = CAST(ST_Y(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{item.londd},#{item.latdd}),4326),5186)) AS FLOAT)
	, SVY_DATA = ST_ASTEXT(ST_Transform(ST_SetSRID(ST_MakePoint(#{item.londd},#{item.latdd}),4326),5186))
	, SVY_ATTR = #{item.svy_attr}
	, LAST_UPDT_PNTTM = now()
	, SVY_MEMO = #{item.svy_memo}

INSERT INTO TN_FEIS_CNL_FIELDINFO
( MST_ID,LOGIN_ID,SVY_KEYWORD,SVY_LABEL,SVY_LON,SVY_LAT,SVY_ATTR,CREAT_DT,LAST_UPDT_PNTTM,SVY_MEMO,SVY_DATA )
VALUES
(
	CAST(#{mst_id} AS INT)
	, #{login_id}
	, #{svy_keyword}
	, #{svy_label}
	, CAST(ST_X(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{londd},#{latdd}),4326),5186)) AS FLOAT)
	, CAST(ST_Y(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{londd},#{latdd}),4326),5186)) AS FLOAT)
	, #{svy_attr}
	, now()
	, now()
	, #{svy_memo}
	, ST_ASTEXT(ST_Transform(ST_SetSRID(ST_MakePoint(#{londd},#{latdd}),4326),5186))
)
ON CONFLICT ON CONSTRAINT TN_FEIS_CNL_FIELDINFO_UN
DO UPDATE
SET SVY_KEYWORD = #{svy_keyword}
	, LOGIN_ID = #{login_id}
	, SVY_LON = CAST(ST_X(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{londd},#{latdd}),4326),5186)) AS FLOAT)
	, SVY_LAT = CAST(ST_Y(ST_TRANSFORM(ST_SetSRID(ST_MakePoint(#{londd},#{latdd}),4326),5186)) AS FLOAT)
	, SVY_DATA = ST_ASTEXT(ST_Transform(ST_SetSRID(ST_MakePoint(#{londd},#{latdd}),4326),5186))
	, SVY_ATTR = #{svy_attr}
	, LAST_UPDT_PNTTM = now()
	, SVY_MEMO = #{svy_memo}

mapper.delete:

DELETE FROM TN_FEIS_CNL_FIELDINFO
WHERE MST_ID = CAST(#{mst_id} as INT)
AND SVY_LABEL = #{label}

mapper.select:
SELECT USER_GRADE FROM TN_FEIS_CNRSSPCE_AUTHORMGT
WHERE MST_ID = #{id}
	AND ESNTL_ID = #{esntlId}
