mapper.select:
SELECT SIGNGU_COD AS adminCode , SIGNGU_NM AS adminNm FROM TF_FEIS_SIGNGU WHERE 1=1 AND CTPRVN_COD = #{sdCode}
AND CTPRVN_NM = #{sdNm}
ORDER BY SIGNGU_NM

SELECT EMD_CODE AS adminCode , EMD_NM AS adminNm FROM TF_FEIS_EMD WHERE 1=1 AND SIGNGU_COD = #{sggCode}
AND CTPRVN_NM = #{sdNm} AND SIGNGU_NM = #{sggNm}
ORDER BY EMD_NM

SELECT  LI_CODE AS adminCode
     ,  LI_NM AS adminNm
  FROM  TF_FEIS_LI
  WHERE 1=1
  AND 
  	EMD_CODE = #{emdCode}

  AND 
  	CTPRVN_NM = #{sdNm}
  	AND
  	SIGNGU_NM = #{sggNm}
  	AND
  	EMD_NM = #{emdNm}

  ORDER BY LI_NM
