mapper.select:

SELECT COUNT(*) AS totcnt
  FROM TN_FEIS_PROGRMLIST
 WHERE PROGRM_FILE_NM = #{searchKeyword}

mapper.insert:

INSERT INTO TN_FEIS_PROGRMLIST 
	( PROGRM_FILE_NM
	  , PROGRM_STRE_PATH
	  , PROGRM_KOREAN_NM
	  , PROGRM_DC
	  , URL )
VALUES ( #{progrmFileNm}
	  , #{progrmStrePath}
	  , #{progrmKoreanNm}
	  , #{progrmDc}
	  , #{URL} )

mapper.update:

UPDATE TN_FEIS_PROGRMLIST 
SET PROGRM_STRE_PATH=#{progrmStrePath}
	, PROGRM_KOREAN_NM=#{progrmKoreanNm}
	, PROGRM_DC      =#{progrmDc}
	, URL            =#{URL}
WHERE PROGRM_FILE_NM=#{progrmFileNm}
